package anusha;

public class class8loopfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i;
System.out.println("even numbers");
for(i=0;i<=15;i+=2)
{
	System.out.println(i);
}
System.out.println("odd numbers");
for(i=1;i<=15;i+=2)
{
	System.out.println(i);}
}
}
	

	


